// Function to check if a word is valid
export function isValidSpanishWord(word: string): boolean {
  return SPANISH_WORDS.has(word.toLowerCase().trim());
}

// Get all words starting with a specific letter
export function getWordsStartingWith(letter: string): string[] {
  const normalizedLetter = letter.toLowerCase();
  return Array.from(SPANISH_WORDS).filter(word => word.startsWith(normalizedLetter));
}

// Get a random starting word
export function getRandomStartingWord(): string {
  const words = Array.from(SPANISH_WORDS).filter(w => w.length >= 4 && w.length <= 8);
  return words[Math.floor(Math.random() * words.length)];
}
